"use strict";

var _commonBackend = require("@dracul/common-backend");

const Schema = _commonBackend.mongoose.Schema;
const colorsSchema = new _commonBackend.mongoose.Schema({
  primary: {
    type: String,
    required: true
  },
  onPrimary: {
    type: String,
    required: true
  },
  secondary: {
    type: String,
    required: true
  },
  onSecondary: {
    type: String,
    required: true
  }
});
const logoSchema = new _commonBackend.mongoose.Schema({
  mode: {
    type: String,
    required: true
  },
  title: {
    type: String,
    required: false
  },
  filename: {
    type: String,
    required: false
  },
  url: {
    type: String,
    required: false
  }
});
const CustomizationSchema = new Schema({
  colors: colorsSchema,
  logo: logoSchema,
  language: {
    type: String,
    required: true
  }
});

const Customization = _commonBackend.mongoose.model('Customization', CustomizationSchema);

module.exports = Customization;