"use strict";

var _commonBackend = require("@dracul/common-backend");

// Defining user Mongoose Schema
const UserAuditSchema = new _commonBackend.mongoose.Schema({
  actionBy: {
    type: _commonBackend.mongoose.Schema.Types.ObjectId,
    ref: 'User',
    unique: false,
    required: false,
    dropDups: true
  },
  actionFor: {
    type: _commonBackend.mongoose.Schema.Types.ObjectId,
    ref: 'User',
    unique: false,
    required: true,
    dropDups: true
  },
  action: {
    type: String,
    unique: false,
    required: true,
    dropDups: true
  },
  date: {
    type: Date,
    required: true,
    default: Date.now
  }
});
UserAuditSchema.set('toJSON', {
  getters: true
});
module.exports = _commonBackend.mongoose.model('UserAudit', UserAuditSchema);